INSERT INTO `design_titles` (`DT_id`, `DT_title`, `D_id`, `DT_demand`, `DT_bonus`, `DT_takers`, `DT_taked`) VALUES
(7, '学生在线请假系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）班主任通过工号密码登陆后台管理，进行班级信息管理、班级名单管理、请假数据汇总、查询管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）班主任可以对本班的学生，进行批假处理、请假数据汇总、查询管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生在前端系统，通过学号登陆，可以进行在线请假、跟踪请假审批进度等操作，在线请假时应当提供必需的佐证材料（如病历图片、活动通知截图等等）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）请<span>2</span>天以内，班主任批假，<span>2-4</span>天辅导员或系主任批假，<span>5</span>天以上，学生处批假。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>2</span>个班主任账号、<span>2</span>个班级名单，<span>20</span>条以上请假信息。<span></span>\r\n</p>', 2, 2, 0),
(8, 'XX社团新闻系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台管理模块，可以进行新闻类别的添加、修改、删除管理，对新闻内容可以进行添加、修改、删除以及修改类别的管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）系统前端按照不同的新闻类别，分栏目显示新闻；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）所有的新闻支持图、文（同时支持音、视频<span>+1</span>分）。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统自动统计每条新闻阅读量。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>3</span>个新闻栏目，<span>30</span>条新闻数据。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）凡需要数据列表显示处，必须分页显示，每页<span>20</span>条。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>7</span>）自行根据条件，决定选择的社团名称。<span></span>\r\n</p>', 1, 2, 2),
(9, '饭堂服务在线点评', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）饭堂经理通过后台管理模块，发布每天的菜单、价格、服务人员及服务窗口；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）前端模块清晰、明确显示每天的饭堂菜单列表、服务人员信息列表，并提供服务点评入口；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过前端系统，学号登陆，对饭堂的菜肴的质量、口感、价，服务人员的工作态度、服务质量、服务效率进行评价<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统在合适的位置，提供方便高效地查看每一天、每一周的点评结果。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）点评结果中，应当提供点评的统计数据（参评人数、好评率、差评率等等）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据中，至少包含<span>5</span>名服务人员、<span>10</span>道菜式、<span>2</span>周内的评价数据（评价人数不少于<span>10</span>人）。<span></span>\r\n</p>', 3, 2, 2),
(10, '学生宿舍报修系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求： <span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台模块，管理宿舍的分布数据（楼号、宿舍号、设备名称……）、维修师傅的个人信息（工号、姓名、性别、电话、维修专长<span>……</span>）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）宿管员通过后台模块登陆，分派维修师傅，查询报修数据，汇总每周、每月的报修、维修数据；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过前端系统，登陆报修，跟踪维修进度；对维修师傅的工作进行评价<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）学生报修时，可自主选择维修师傅，也可以由管理人员分派；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）前端清晰显示每周、每月所有维修师傅的维修评分情况列表；）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据中，至少包含<span>2</span>幢楼房、每幢<span>5</span>间宿舍、<span>3</span>名维修师傅、<span>10</span>条以上的报修数据。<span></span>\r\n</p>', 3, 2, 2),
(11, 'XX微商城', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过登陆后台模块，能够管理商品信息（发布、修改、下架等）、管理理顾客订单（发货、撤销订单等等）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）普通客户通过注册成为微商城会员；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）会员可以将商品加入购物车、管理购物车、下订单、结算订单（支付功能虚拟实现）、对商品评价<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统前端显示商品列表、商品详情，提供会员登陆入口<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）会员可以查看自己的消费记录。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）自行决定商城名称<span></span>\r\n</p>', 4, 2, 2),
(12, '小型成绩管理系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过工号密码，登陆后台模块，管理教师信息（工号、姓名、任课班级、任课科目<span>……</span>）、学生信息（学号、班级、专业<span>……</span>）、课程信息（班级编号、课程编号、课程名称、任课老师<span>……</span>）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）教师通过工号密码，登陆后台模块登陆，管理所任教的班级成绩（录入、修改、查询）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过学号登陆系统，查看自己各学期各科成绩<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统自动计算每位学生每学期的总分以及班内排名，并在学生的查询结果中显示。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>3</span>位老师、<span>2</span>个班级、<span>3</span>门课程、<span>2</span>个学期的成绩数据。<span></span>\r\n</p>', 5, 2, 2),
(13, '考证在线报名系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）管理人员通过后台模块，管理（增加、修改、删除）考证项目（考证项目、报名截止时间、考试时间、考试地点、考试费用<span>……</span>），汇总报考人员信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）系统自动判断截止时间，超期不再接受报名操作；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过前端进入相应的考证项目报名页，填写并提交自己的报名信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）学生通过前端查询自己已报名的考证项目；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>5</span>考证项目、<span>40</span>条报名信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）凡需要显示数据列表处，必须分页显示，每页<span>20</span>条；<span></span>\r\n</p>', 3, 2, 2),
(14, '惠州汽车客运站客车班次查询', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台模块，管理客车班次信息（站点、时间、班次、票价、余票……）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）乘客不需登录，通过前端接口，根据上车站点、下车站点、出发时间，查询相应班车；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）在上车站与下车站之间没有直达班车时，系统能够自动推荐转乘计划。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）相关数据可从惠州市汽运站官网获取，要求测试数据数据中，班车信息至少覆盖广东省所有地级市，不少于<span>50</span>班汽车信息<span></span>\r\n</p>', 3, 2, 0),
(15, '惠州市火车站列车班次查询', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台模块，管理列车班次信息（车型、站点、出发时间、班次、票价等等）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）乘客不需登录，通过前端接口，根据上车站点、下车站点、出发时间，查询相应列车班次；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）在上车站与下车站之间没有直达班车时，系统能够自动推荐转乘计划。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）相关数据可从铁道部官网获取，要求测试数据数据中，列车信息至少覆盖全国每个省份两个以上主要城市，不少于<span>50</span>次列车信息。<span></span>\r\n</p>', 3, 2, 1),
(16, '惠城院学生评教系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过后台模块，管理学生信息（学号、班级、专业……）、教师信息（教师工号、姓名、密码、任课班级、任课科目<span>……</span>）、管理评教活动（开<span>/</span>关评教活动、设置评教时间、设置评教项目内容）、查看汇总评教结果；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）学生通过学号登陆，查看已开启的评教活动，在相应的评教活动中对自己所在班级的任课老师，进行匿名评教操作（<span>100</span>分制或五星制）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）教师通过工号登陆，查看自己的评教结果。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统自动汇总统计每个教师的评教结果（参评人数、好评率或平均分、存在问题与建议<span>……</span>）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>1</span>个班级、<span>3</span>门课程、<span>3</span>名教师、<span>30</span>名学生的评教数据。<span></span>\r\n</p>', 4, 2, 2),
(17, '惠城院投票系统(多项目）', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台模块，管理投票项目（项目名称、投票选项、设置投票条件、投票限制、截止时间……）查看投票结果数据；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）投票人员通过前端注册、登陆以及查看可参与的投票活动，并在相应的活动中投票；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）系统根据相关设置，决定是否在前端显示最新的投票结果；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统支持图文投票内容。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）系统在“投票限制”上支持<span>“</span>单日限次数<span>”</span>与<span>“</span>单日不限次数<span>”</span>两种模式。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）<span>“</span>投票条件<span>”</span>支持“需登陆后投票<span>”</span>与<span>“</span>不需登陆投票<span>”</span>两种\r\n</p>', 4, 2, 2),
(18, '小型图书管理系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）图书管理员通过后台模块，管理图书信息（图书分类、图书名、作者、封面图片、<span>……</span>分馆号、书架号……）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）读者通过前端查看最新上架图书、根据条件查询所需图书的信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）前端支持图书列表按图书分类显示、按馆架号显示；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）至少支持读者按书名、作者与<span>ISBN</span>三种条件查询；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，图书分类参考《国家图书馆分类》设置，至少覆盖<span>15</span>种以上分类，每种分类中图书数量不少于<span>20</span>本，馆室归类不少于<span>5</span>种，书架不少于<span>15</span>具。\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）所有图书列表的显示，必须分页显示，每页最多显示<span>12</span>本。<span></span>\r\n</p>', 3, 2, 2),
(19, '小型在线考试系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教师通过工号登陆后台，导入（添加）考试学生名单，进行命题操作、设置考试时间、班级，查看学生的考试成绩；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）学生前端登陆，选择要参与的考试科目，进行答题操作，系统自动倒计时；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）学生可自主提前结束考试并交卷，倒计时结束时系统自动提交学生的答案；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）每位学生交卷完成后，系统自动评分，教师在后台可查看分数，发布分数。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）教师发布学生分数以后，每位学生可登陆查看自己的考试成绩。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>7</span>）测试数据中，至少已有<span>1</span>科考试题目，<span>1</span>个班的学生名单，<span>5</span>名学生的考试结果。\r\n</p>', 6, 2, 2),
(20, '惠城院在线选课系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过后台模块，管理可供选修的课程信息（课程名称，任课教师，开班人数，学分，上课时间、面向院系……）查看每门课程的选修结果；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）学生通过前端登陆，查看所有可选修的课程信息，进行选课操作，查看已选修课程的最新动态（选修人数、是否开班……）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）课程列表分页显示，每页最多显示<span>15</span>条；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）每个学生每学期限选<span>4</span>学分，已选修成功的课程累计学分达到<span>4</span>学分时，系统不允许该生再进行选课操作；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少<span>40</span>门课程供选修，面向<span>10</span>个院、系。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）系统根据每门课的选修人数以及开班人数，自动判定该门课是否开班。\r\n</p>', 5, 2, 2),
(21, '课堂考勤系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）每个教师可注册自己的个人账号，并添加自己的任课班级以及班级名单；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）教师在系统中对学生进行考勤情况登记（正常、旷课、迟到、病假、事假）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过学号登陆，可查询自己所有的课堂出勤数据；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统自动汇总每个学生每门课程的出勤情况，形成统计报表；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少<span>1</span>个班级，<span>3</span>门课程，<span>30</span>名学生，<span>18</span>节课的考勤数据。<span></span>\r\n</p>', 4, 2, 1),
(22, '惠城院来访登记系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）门卫通过工号与密码，登陆系统，进行来访登记操作。门卫的只有查询一周内来访记录以及登记来访情况的权限，没有删除来访记录的权限。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）来访信息包括（来访者姓名、证件号码、电话、访问部门、来访时间、接待人姓名、离开时间、登记人）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）系统管理员通过管理账号，登陆系统；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统管理员可以查询任意时间的来访记录，删除记录；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）所有需要显示的数据列表处，分页显示，每页显示<span>20</span>条。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据至少<span>2</span>个门卫账号，<span>50</span>条来访信息。<span></span>\r\n</p>', 3, 2, 2),
(23, '惠城院教学日志系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过管理账号，登陆系统，可查询全部老师的全部教学日志；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）教务管理员可发布、修改每学期的各班课程表安排（班级、时间、课程、任课老师……）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）教师通过工号与密码，登陆系统，只能完成当天的教学日志填写（包括课时、授课班级、授课章节、出勤人数、缺课或请假名单。系统自动判定当前的日期，提供该日期内该老师的任课信息供教师选择。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）教师可以查询自己所有的教学日志，但只能修改自己当天的教学日志；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）所有需要显示的数据列表处，分页显示，每页显示<span>20</span>条。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据至少<span>1</span>个班级的全部课程<span>2</span>个周的教学日志。<span></span>\r\n</p>', 4, 2, 2),
(24, '惠城院“问吧”', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统的功能目标：提供一个在线问答系统（参考百度问吧），利用网络力量，帮助惠城职院的师生解决他们的疑问。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）系统管理员可添加、修改、删除问题栏目；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）所有人都可以注册一个系统账号，登陆后，在相应的栏目提问或对某个问题回答；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）每个人都可以查询自己提问过的问题或回答过的问题，可以修改自己的问题或回答；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）非注册用户，可以查看所有问题与答案，但不能提问与回答。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）所有的数据列表处，分页显示，每页<span>20</span>条。测试数据至少<span>3</span>个栏目，每个栏目已有<span>5</span>个问题。<span></span>\r\n</p>', 3, 2, 0),
(25, '惠城院考场管理系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过工号密码登陆，有权添加、修改、删除每学期的考场信息（楼号、教室号、日期时间、考试科目、监考老师、考试班级、考试人数<span>……</span>）。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）教师不需登陆，根据姓名或工号，可查询自己的监考考场信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生不需登陆，可根据学号或班级查询相关的考试安排信息；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）所有的数据列表处，分页显示，每页<span>20</span>条。测试数据至少<span>3</span>个栏目，每个栏目已有<span>5</span>个问题。<span></span>\r\n</p>', 2, 2, 1),
(26, '惠城院流动课室管理系统', 4, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）教务管理员通过工号密码登陆，有权添加、修改、删除每学期的流动课室安排（楼号、教室号、日期时间、上课班级、任课老师、上课科目、上课人数<span>……</span>）。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）教师不需登陆，根据姓名、工号或上课班级，查询自己的上课教室；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）教师通过工号密码登陆，通过填写上课教室申请表（上课时间、班级、人数），申请临时上课教室。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统根据已有的流动课室安排情况，自动推荐符合教师申请要求的教室供教师选择，并自动保存该次课室安排数据。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）学生不需登陆，可根据学号、班级或上课时间，查询自己的上课教室；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）所有的数据列表处，分页显示，每页<span>20</span>条。测试数据至少<span>3</span>个栏目，每个栏目已有<span>5</span>个问题。<span></span>\r\n</p>', 4, 2, 2),
(27, '学生在线请假系统', 3, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）班主任通过工号密码登陆后台管理，进行班级信息管理、班级名单管理、请假数据汇总、查询管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）班主任可以对本班的学生，进行批假处理、请假数据汇总、查询管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生在前端系统，通过学号登陆，可以进行在线请假、跟踪请假审批进度等操作，在线请假时应当提供必需的佐证材料（如病历图片、活动通知截图等等）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）请<span>2</span>天以内，班主任批假，<span>2-4</span>天辅导员或系主任批假，<span>5</span>天以上，学生处批假。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>2</span>个班主任账号、<span>2</span>个班级名单，<span>20</span>条以上请假信息。<span></span>\r\n</p>', 2, 2, 2),
(28, 'XX社团新闻系统', 3, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台管理模块，可以进行新闻类别的添加、修改、删除管理，对新闻内容可以进行添加、修改、删除以及修改类别的管理；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）系统前端按照不同的新闻类别，分栏目显示新闻；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）所有的新闻支持图、文（同时支持音、视频<span>+1</span>分）。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统自动统计每条新闻阅读量。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）测试数据中，至少包含<span>3</span>个新闻栏目，<span>30</span>条新闻数据。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）凡需要数据列表显示处，必须分页显示，每页<span>20</span>条。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>7</span>）自行根据条件，决定选择的社团名称。<span></span>\r\n</p>', 1, 2, 2),
(29, '饭堂服务在线点评', 3, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求：<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）饭堂经理通过后台管理模块，发布每天的菜单、价格、服务人员及服务窗口；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）前端模块清晰、明确显示每天的饭堂菜单列表、服务人员信息列表，并提供服务点评入口；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过前端系统，学号登陆，对饭堂的菜肴的质量、口感、价，服务人员的工作态度、服务质量、服务效率进行评价<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）系统在合适的位置，提供方便高效地查看每一天、每一周的点评结果。<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）点评结果中，应当提供点评的统计数据（参评人数、好评率、差评率等等）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据中，至少包含<span>5</span>名服务人员、<span>10</span>道菜式、<span>2</span>周内的评价数据（评价人数不少于<span>10</span>人）。<span></span>\r\n</p>', 3, 2, 2),
(30, '学生宿舍报修系统', 3, '<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	基本要求： <span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>1</span>）系统管理员通过后台模块，管理宿舍的分布数据（楼号、宿舍号、设备名称……）、维修师傅的个人信息（工号、姓名、性别、电话、维修专长<span>……</span>）；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>2</span>）宿管员通过后台模块登陆，分派维修师傅，查询报修数据，汇总每周、每月的报修、维修数据；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>3</span>）学生通过前端系统，登陆报修，跟踪维修进度；对维修师傅的工作进行评价<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>4</span>）学生报修时，可自主选择维修师傅，也可以由管理人员分派；<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>5</span>）前端清晰显示每周、每月所有维修师傅的维修评分情况列表；）<span></span>\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:5.4pt;">\r\n	（<span>6</span>）测试数据中，至少包含<span>2</span>幢楼房、每幢<span>5</span>间宿舍、<span>3</span>名维修师傅、<span>10</span>条以上的报修数据。<span></span>\r\n</p>', 3, 2, 2),
